import React, { useState, useEffect, useRef, useCallback } from 'react';
import './App.css';

const API_BASE_URL = 'http://localhost:8003';
const WS_URL = 'ws://localhost:8003/ws';

function App() {
  const [currentFrame, setCurrentFrame] = useState(null);
  const [stats, setStats] = useState({
    total_frames: 0,
    total_humans: 0,
    current_human_count: 0,
    processing_status: 'idle',
    current_video: null
  });
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [websocketConnected, setWebsocketConnected] = useState(false);

  const fileInputRef = useRef(null);
  const websocketRef = useRef(null);
  const statsIntervalRef = useRef(null);

  // Clear messages after 5 seconds
  useEffect(() => {
    if (error || success) {
      const timer = setTimeout(() => {
        setError(null);
        setSuccess(null);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error, success]);

  // WebSocket connection
  const connectWebSocket = useCallback(() => {
    if (websocketRef.current?.readyState === WebSocket.OPEN) {
      return;
    }

    try {
      websocketRef.current = new WebSocket(WS_URL);

      websocketRef.current.onopen = () => {
        console.log('WebSocket connected');
        setWebsocketConnected(true);
      };

      websocketRef.current.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          if (data.type === 'frame_update') {
            setCurrentFrame({
              frameData: data.frame_data,
              timestamp: data.timestamp,
              humanCount: data.human_count
            });

            // Update current human count in stats
            setStats(prev => ({
              ...prev,
              current_human_count: data.human_count
            }));
          }
        } catch (err) {
          console.error('Error parsing WebSocket message:', err);
        }
      };

      websocketRef.current.onclose = () => {
        console.log('WebSocket disconnected');
        setWebsocketConnected(false);
        // Attempt to reconnect after 3 seconds
        setTimeout(connectWebSocket, 3000);
      };

      websocketRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setWebsocketConnected(false);
      };
    } catch (err) {
      console.error('Failed to create WebSocket connection:', err);
      setWebsocketConnected(false);
    }
  }, []);

  // Fetch stats from API
  const fetchStats = useCallback(async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/stats`);
      if (response.ok) {
        const data = await response.json();
        console.log('Stats update:', data); // Debug log
        setStats(data);
      }
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  }, []);

  // Start stats polling
  const startStatsPolling = useCallback(() => {
    if (statsIntervalRef.current) {
      clearInterval(statsIntervalRef.current);
    }

    fetchStats(); // Fetch immediately
    statsIntervalRef.current = setInterval(fetchStats, 2000); // Poll every 2 seconds
  }, [fetchStats]);

  // Stop stats polling
  const stopStatsPolling = useCallback(() => {
    if (statsIntervalRef.current) {
      clearInterval(statsIntervalRef.current);
      statsIntervalRef.current = null;
    }
  }, []);

  // Initialize connections
  useEffect(() => {
    connectWebSocket();
    startStatsPolling();

    return () => {
      if (websocketRef.current) {
        websocketRef.current.close();
      }
      stopStatsPolling();
    };
  }, [connectWebSocket, startStatsPolling, stopStatsPolling]);

  // Handle file upload
  const handleFileUpload = async (file) => {
    if (!file) return;

    // Validate file type
    const supportedFormats = ['.mp4', '.avi', '.mov', '.mkv', '.webm'];
    const fileExtension = '.' + file.name.split('.').pop().toLowerCase();

    if (!supportedFormats.includes(fileExtension)) {
      setError(`Unsupported format. Supported: ${supportedFormats.join(', ')}`);
      return;
    }

    setIsUploading(true);
    setError(null);
    setSuccess(null);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch(`${API_BASE_URL}/api/upload`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setUploadedFile({
          filename: data.filename,
          originalName: file.name
        });
        setSuccess(`Video uploaded successfully: ${file.name}`);

        // Automatically start processing
        await startProcessing(data.filename);
      } else {
        setError(data.detail || 'Upload failed');
      }
    } catch (err) {
      setError('Failed to upload video: ' + err.message);
    } finally {
      setIsUploading(false);
    }
  };

  // Start video processing
  const startProcessing = async (filename) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/start-stream`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ filename }),
      });

      const data = await response.json();

      if (response.ok) {
        setSuccess('Video processing started');
        setCurrentFrame(null); // Clear previous frame
      } else {
        setError(data.detail || 'Failed to start processing');
      }
    } catch (err) {
      setError('Failed to start processing: ' + err.message);
    }
  };

  // Handle drag and drop
  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add('dragover');
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove('dragover');
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove('dragover');

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  // Handle file input change
  const handleFileInputChange = (e) => {
    const files = e.target.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  // Download processed video
  const handleDownload = () => {
    if (uploadedFile?.filename) {
      const downloadUrl = `${API_BASE_URL}/api/download/${uploadedFile.filename}`;
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = `processed_${uploadedFile.originalName}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  // Determine if download should be enabled - FIXED
  const isDownloadEnabled = stats.processing_status === 'completed';
  const isProcessing = stats.processing_status === 'processing';

  return (
    <div className="app">
      <header className="header">
        <h1>🎥 Video Detection Streaming</h1>
        <p>Upload videos and watch real-time human detection processing</p>
      </header>

      {error && (
        <div className="error-message">
          ❌ {error}
        </div>
      )}

      {success && (
        <div className="success-message">
          ✅ {success}
        </div>
      )}

      <div className="main-container">
        {/* Top Controls - Upload Button */}
        <div className="top-controls">
          <div className="upload-button-container">
            <button
              className="upload-button-main"
              onClick={() => fileInputRef.current?.click()}
              disabled={isUploading}
            >
              {isUploading ? (
                <>
                  <div className="loading"></div>
                  Uploading...
                </>
              ) : (
                <>
                  📁 Upload Video
                </>
              )}
            </button>
            <input
              ref={fileInputRef}
              type="file"
              className="file-input"
              accept=".mp4,.avi,.mov,.mkv,.webm"
              onChange={handleFileInputChange}
              disabled={isUploading}
            />
          </div>
        </div>

        {/* Full Width Video Display */}
        <div className="video-display-section">
          <h2>🎬 Real-Time Human Detection Stream</h2>
          <div className="video-container">
            {currentFrame ? (
              <img
                src={`data:image/jpeg;base64,${currentFrame.frameData}`}
                alt="Processed frame with human detection bounding boxes"
                className="video-frame"
              />
            ) : (
              <div className="video-placeholder">
                {isProcessing ? 
                  '🔄 AI is processing your video frames in real-time...\nHuman detection results will appear here' : 
                  '⏸️ Click "Upload Video" above to start real-time human detection\nSupported formats: MP4, AVI, MOV, MKV, WEBM'
                }
              </div>
            )}
          </div>

          {/* Download Button Below Video */}
          <div className="download-container">
            <button
              className="download-button"
              onClick={handleDownload}
              disabled={!isDownloadEnabled || !uploadedFile}
            >
              {isDownloadEnabled && uploadedFile ? '⬇️ Download Processed Video' : 
               stats.processing_status === 'completed' ? '⬇️ Download Ready' :
               stats.processing_status === 'processing' ? '⏳ Processing Video...' : 
               '📤 Upload video first'}
            </button>
          </div>
        </div>
      </div>

      <div className="stats-container">
        <div className="stats-grid">
          <div className="stat-item">
            <div className="stat-value">{stats.current_human_count}</div>
            <div className="stat-label">Current Humans</div>
          </div>
          <div className="stat-item">
            <div className="stat-value">{stats.total_humans}</div>
            <div className="stat-label">Total Detected</div>
          </div>
          <div className="stat-item">
            <div className="stat-value">{stats.total_frames}</div>
            <div className="stat-label">Frames Processed</div>
          </div>
          <div className="stat-item">
            <div className="stat-value">
              {websocketConnected ? '🟢' : '🔴'}
            </div>
            <div className="stat-label">WebSocket</div>
          </div>
        </div>
      </div>

      <div className="status-bar">
        <div className="status-indicator">
          <div className={`status-dot ${stats.processing_status}`}></div>
          <span className="status-text">
            Status: {stats.processing_status.charAt(0).toUpperCase() + stats.processing_status.slice(1)}
          </span>
        </div>
        {stats.current_video && (
          <div className="current-video">
            📹 Current video: {stats.current_video}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
