import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Upload, Download, Wifi, WifiOff, AlertCircle, CheckCircle, Clock } from 'lucide-react';
import './App.css';

const App = () => {
  // State management
  const [uploadStatus, setUploadStatus] = useState('idle'); // idle, uploaded, processing, completed, error
  const [isConnected, setIsConnected] = useState(false);
  const [currentFrame, setCurrentFrame] = useState(null);
  const [stats, setStats] = useState({
    currentHumans: 0,
    totalHumans: 0,
    totalFrames: 0,
    processingTime: "00:00:00"
  });
  const [uploadedFileName, setUploadedFileName] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Refs
  const fileInputRef = useRef(null);
  const wsRef = useRef(null);
  const statsIntervalRef = useRef(null);

  // Configuration
  const API_BASE = 'http://localhost:8003';
  const WS_URL = 'ws://localhost:8003/ws';
  const SUPPORTED_FORMATS = ['mp4', 'avi', 'mov', 'mkv', 'webm'];

  // Utility functions
  const showError = (message) => {
    setError(message);
    setTimeout(() => setError(''), 5000);
  };

  const showSuccess = (message) => {
    setSuccess(message);
    setTimeout(() => setSuccess(''), 3000);
  };

  const validateFile = (file) => {
    if (!file) return 'No file selected';

    const fileExtension = file.name.split('.').pop().toLowerCase();
    if (!SUPPORTED_FORMATS.includes(fileExtension)) {
      return `Unsupported format. Please use: ${SUPPORTED_FORMATS.join(', ').toUpperCase()}`;
    }

    // Check file size (limit to 500MB)
    const maxSize = 500 * 1024 * 1024; // 500MB in bytes
    if (file.size > maxSize) {
      return 'File size too large. Maximum size is 500MB';
    }

    return null;
  };

  // WebSocket functions
  const connectWebSocket = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) return;

    try {
      wsRef.current = new WebSocket(WS_URL);

      wsRef.current.onopen = () => {
        console.log('WebSocket connected');
        setIsConnected(true);
      };

      wsRef.current.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          if (data.type === 'frame' && data.image) {
            setCurrentFrame(`data:image/jpeg;base64,${data.image}`);
          }

          if (data.type === 'stats') {
            setStats(prevStats => ({
              ...prevStats,
              currentHumans: data.current_humans || 0,
              totalHumans: data.total_humans || prevStats.totalHumans,
              totalFrames: data.total_frames || prevStats.totalFrames
            }));
          }

          if (data.type === 'status') {
            setUploadStatus(data.status);
            if (data.status === 'completed') {
              showSuccess('Video processing completed successfully!');
              stopStatsPolling();
            }
          }

        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      wsRef.current.onclose = () => {
        console.log('WebSocket disconnected');
        setIsConnected(false);

        // Auto-reconnect if processing
        if (uploadStatus === 'processing') {
          setTimeout(connectWebSocket, 2000);
        }
      };

      wsRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };

    } catch (error) {
      console.error('Failed to connect WebSocket:', error);
      setIsConnected(false);
    }
  }, [uploadStatus]);

  // API functions
  const uploadFile = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch(`${API_BASE}/api/upload`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ detail: 'Upload failed' }));
        throw new Error(errorData.detail || 'Upload failed');
      }

      const result = await response.json();
      setUploadedFileName(result.filename || file.name);
      setUploadStatus('uploaded');
      showSuccess('File uploaded successfully!');

      // Auto-start processing
      await startProcessing();

    } catch (error) {
      console.error('Upload error:', error);
      showError(error.message || 'Upload failed');
      setUploadStatus('error');
    }
  };

  const startProcessing = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/start-stream`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ filename: uploadedFileName }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ detail: 'Processing failed to start' }));
        throw new Error(errorData.detail || 'Processing failed to start');
      }

      setUploadStatus('processing');
      connectWebSocket();
      startStatsPolling();
      showSuccess('Processing started!');

    } catch (error) {
      console.error('Start processing error:', error);
      showError(error.message || 'Failed to start processing');
      setUploadStatus('error');
    }
  };

  const fetchStats = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/stats`);
      if (response.ok) {
        const data = await response.json();
        setStats(prevStats => ({
          currentHumans: data.current_humans || 0,
          totalHumans: data.total_humans || prevStats.totalHumans,
          totalFrames: data.total_frames || prevStats.totalFrames,
          processingTime: data.processing_time || prevStats.processingTime
        }));

        // Check if processing is complete
        if (data.status === 'completed' && uploadStatus === 'processing') {
          setUploadStatus('completed');
          stopStatsPolling();
        }
      }
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const downloadProcessedVideo = async () => {
    if (!uploadedFileName || uploadStatus !== 'completed') return;

    try {
      const response = await fetch(`${API_BASE}/api/download/${uploadedFileName}`);

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = uploadedFileName.replace(/\.[^/.]+$/, '_processed.mp4');
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      showSuccess('Download started!');

    } catch (error) {
      console.error('Download error:', error);
      showError('Failed to download processed video');
    }
  };

  // Polling functions
  const startStatsPolling = () => {
    stopStatsPolling(); // Clear any existing interval
    statsIntervalRef.current = setInterval(fetchStats, 2000);
  };

  const stopStatsPolling = () => {
    if (statsIntervalRef.current) {
      clearInterval(statsIntervalRef.current);
      statsIntervalRef.current = null;
    }
  };

  // Event handlers
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const validationError = validateFile(file);
    if (validationError) {
      showError(validationError);
      return;
    }

    setUploadStatus('idle');
    uploadFile(file);
  };

  const handleUploadClick = () => {
    fileInputRef.current?.click();
  };

  const handleDragOver = (event) => {
    event.preventDefault();
    event.stopPropagation();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    event.stopPropagation();

    const files = event.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      const validationError = validateFile(file);
      if (validationError) {
        showError(validationError);
        return;
      }

      setUploadStatus('idle');
      uploadFile(file);
    }
  };

  // Status indicator component
  const StatusIndicator = () => (
    <div className="flex items-center gap-2">
      {uploadStatus === 'idle' && <Clock className="w-4 h-4 text-gray-400" />}
      {uploadStatus === 'uploaded' && <CheckCircle className="w-4 h-4 text-green-500" />}
      {uploadStatus === 'processing' && (
        <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin" />
      )}
      {uploadStatus === 'completed' && <CheckCircle className="w-4 h-4 text-green-600" />}
      {uploadStatus === 'error' && <AlertCircle className="w-4 h-4 text-red-500" />}
      <span className="font-medium capitalize text-sm">{uploadStatus}</span>
    </div>
  );

  const ConnectionStatus = () => (
    <div className="flex items-center gap-2">
      {isConnected ? (
        <>
          <Wifi className="w-4 h-4 text-green-500" />
          <span className="text-green-600 text-sm">Connected</span>
        </>
      ) : (
        <>
          <WifiOff className="w-4 h-4 text-red-500" />
          <span className="text-red-600 text-sm">Disconnected</span>
        </>
      )}
    </div>
  );

  // Cleanup on component unmount
  useEffect(() => {
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      stopStatsPolling();
    };
  }, []);

  // Auto-connect WebSocket when processing starts
  useEffect(() => {
    if (uploadStatus === 'processing' && !isConnected) {
      connectWebSocket();
    }
  }, [uploadStatus, isConnected, connectWebSocket]);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        <header className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Real-time Video Detection Service
          </h1>
          <p className="text-gray-600">
            Upload videos for human detection analysis with live streaming results
          </p>
        </header>

        {/* Error/Success Messages */}
        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center gap-2">
            <AlertCircle className="w-5 h-5 text-red-500" />
            <span className="text-red-700">{error}</span>
          </div>
        )}

        {success && (
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center gap-2">
            <CheckCircle className="w-5 h-5 text-green-500" />
            <span className="text-green-700">{success}</span>
          </div>
        )}

        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          {/* Compact Control Panel */}
          <div className="bg-white rounded-lg border border-gray-200 p-4 mb-4">
            <div className="flex flex-wrap items-center justify-between gap-4">
              <div className="flex items-center gap-4">
                <input
                  type="file"
                  ref={fileInputRef}
                  onChange={handleFileSelect}
                  accept={SUPPORTED_FORMATS.map(format => `.${format}`).join(',')}
                  style={{ display: 'none' }}
                />
                <button 
                  onClick={handleUploadClick}
                  disabled={uploadStatus === 'processing'}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
                >
                  <Upload className="w-4 h-4 inline mr-2" />
                  Upload
                </button>
                <StatusIndicator />
                <ConnectionStatus />
              </div>
              <div className="flex items-center gap-4 text-sm">
                <span><strong>{stats.currentHumans}</strong> Current</span>
                <span><strong>{stats.totalHumans}</strong> Total</span>
                <span><strong>{stats.totalFrames}</strong> Frames</span>
                <span><strong>{stats.processingTime}</strong></span>
                <button 
                  onClick={downloadProcessedVideo}
                  disabled={uploadStatus !== 'completed'}
                  className={`px-3 py-1 rounded font-medium transition-colors ${
                    uploadStatus === 'completed' 
                      ? 'bg-green-600 hover:bg-green-700 text-white' 
                      : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  }`}
                >
                  <Download className="w-4 h-4 inline mr-1" />
                  Download
                </button>
              </div>
            </div>
          </div>

          {/* Video Display */}
          <div 
            className="bg-black rounded-lg flex items-center justify-center relative h-96"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            {currentFrame ? (
              <img 
                src={currentFrame} 
                alt="Processed frame with detections" 
                className="max-w-full max-h-full object-contain"
              />
            ) : (
              <div className="text-white text-center">
                <Upload className="w-16 h-16 mx-auto mb-4 opacity-50" />
                <p className="text-lg">Drop video file here or click Upload</p>
                <p className="text-sm opacity-75">
                  Supports: {SUPPORTED_FORMATS.join(', ').toUpperCase()}
                </p>
                <p className="text-xs opacity-50 mt-2">
                  Real-time detection with bounding boxes will appear here
                </p>
              </div>
            )}
            {uploadStatus === 'processing' && (
              <div className="absolute top-4 left-4 bg-red-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                ‚óè LIVE
              </div>
            )}
            {uploadedFileName && (
              <div className="absolute bottom-4 left-4 bg-black bg-opacity-75 text-white px-3 py-1 rounded text-sm">
                {uploadedFileName}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
